import {
  InputGroup,
  InputGroupAddon,
} from 'components/selia/input-group';
import { Textarea } from 'components/selia/textarea';
import { Menu, MenuTrigger, MenuPopup, MenuItem, MenuSeparator } from 'components/selia/menu';
import { Button } from 'components/selia/button';
import { PlusIcon, PaperclipIcon, ArrowUpIcon, BrainIcon } from 'lucide-react';
import { Select, SelectTrigger, SelectValue, SelectPopup, SelectItem, SelectList } from 'components/selia/select';
import { Switch } from "components/selia/switch";

export function Editor() {
  return (
    <InputGroup variant="subtle" className="shadow-card ring-card-border">
      <Textarea placeholder="Ask AI anything ..." className="resize-none" />
      <InputGroupAddon align="block-end">
        <Menu>
          <MenuTrigger
            render={
              <Button size="sm-icon" variant="outline" pill>
                <PlusIcon />
              </Button>
            }
          />
          <MenuPopup align="start" className="w-52">
            <MenuItem>
              <PaperclipIcon /> Add from local files
            </MenuItem>
            <MenuItem>
              <DriveIcon />
              Open Google Drive
            </MenuItem>
            <MenuSeparator />
            <MenuItem render={<label />} closeOnClick={false}>
              <BrainIcon />
              Thinking
              <Switch />
            </MenuItem>
            <MenuItem render={<label />} closeOnClick={false}>
              <BrainIcon />
              Web Search
              <Switch />
            </MenuItem>
          </MenuPopup>
        </Menu>
        <Select defaultValue={models[1]} items={models}>
          <SelectTrigger className="ml-auto w-auto mr-2" variant="plain">
            <SelectValue placeholder="Model" />
          </SelectTrigger>
          <SelectPopup>
            <SelectList>
              {models.map((model) => (
                <SelectItem key={model.value} value={model}>
                  {model.icon}
                  {model.label}
                </SelectItem>
              ))}
            </SelectList>
          </SelectPopup>
        </Select>
        <Button size="sm-icon" pill>
          <ArrowUpIcon />
        </Button>
      </InputGroupAddon>
    </InputGroup>
  );
}

